mapper.update:
update sys_role
set description = #{description,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  modify_time = #{modifyTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, description, create_time, modify_time
from sys_role
where id = #{id,jdbcType=VARCHAR}
